        "id": "correlationKeyPayload",
        "label": "Correlation key (payload)",
        "description": "Extracts the correlation key from the incoming message payload",
        "group": "correlation",
        "binding": {
          "name": "correlationKeyExpression",
          "type": "zeebe:property"
        },
        "type": "Hidden",
        "value": "=pollingData.id"
